INSERT INTO `punto_cd` (`idpunto_cd`, `indice`, `punto_cd`, `idetapa_cd`) VALUES
(1, '1.-', 'La contratación se encuentra incluida en el Plan Operativo Anual (POA).', 1),
(2, '2.-', 'La Contratación de Bienes y Servicios fue solicitada en la fecha programada e inscrita en el Programa Anual de Contrataciones.', 1),
(3, '3.-', 'Se estimó el precio referencial para la contratación.', 1),
(4, '4.-', 'Se elaboraron las Especificaciones Técnicas / Términos de Referencia, definiendo el método de selección de adjudicación, e incluyendo condiciones de calidad del bien o servicio, lugar, plazo de entrega, forma de pago, formalización y multas, si corresponde.', 1),
(5, '5.-', 'Se verificó la existencia de saldo presupuestario para la contratación de acuerdo al POA.', 1),
(6, '6.-', 'La invitación a los proponentes se realizó considerando el rubro y capacidad de cumplimiento de los TDR´s.', 2),
(7, '7.-', 'El proponente adjudicado, presentó propuesta o cotización en cumplimiento de las Especificaciones Técnicas / Términos de Referencia.', 2),
(8, '8.-', 'El proponente adjudicado se encuentra registrado en el Registro Único de Proveedores del Estado (RUPE), y no tiene impedimento para participar en procesos de contratación.', 2),
(9, '9.-', 'El proponente se encuentra registrado como deudor en el Sistema Integral de Pensiones y el Sistema de Contribuciones al Seguro Social Obligatorio de largo plazo.', 2),
(10, '10.-', 'La Empresa proponente tiene cuentas pendientes con el Estado. ', 2),
(11, '11.-', 'El contrato incluye en su contenido lo establecido en el Artículo 87 del Decreto Supremo 181.', 2),
(12, '12.-', 'La contratación del bien/prestación de servicio, con plazo de entrega no mayor a 15 días calendario o el establecido en Reglamento Específico, que haya sido formalizada mediante Orden de Compra o Servicio.', 2),
(13, '13.-', 'Las garantías otorgadas por el proponente son de carácter de renovable, irrevocable y de ejecución inmediata.', 2),
(14, '14.-', 'Otros documentos adicionales que se consideren pertinentes.', 2);
